<script>
   function view(th){
      
      let id_bl = th.getAttribute("data-href");
      let transferId = $(".modModal");
      $("#myModal_view").modal("show");
      let tableInner = "";
      let tableBl = "";
      let total = 0;
      
      $.ajax({
         url: BASE_URL + "administrator/bon_livraison/getDetail/" + id_bl,
         type: 'POST',
         dataType: 'json',
         // data: code_pkl
      })
      .done(function(res){
         // console.log(res.bon_livraison_details)
         let tableBlData = Object.values(res.bon_livraison);
         let tableBlDetail = Object.values(res.bon_livraison_details);

         tableBlDetail.forEach(function(item) {
            total += item.PRIX_TOTAL_BLD;
            tableInner += "<tr>";
            tableInner += "<td class='text-center'>";
            tableInner += item.CODE_PRODUIT_BLD;
            tableInner += "</td>";
            tableInner += "<td class='text-center'>";
            tableInner += item.NOM_PRODUIT_BLD;
            tableInner += "</td>";
            tableInner += "<td class='text-center'>";
            tableInner += item.PRIX_UNITAIRE_BLD;
            tableInner += "</td>";
            tableInner += "<td class='text-center'>";
            tableInner += item.QUANTITE_BLD;
            tableInner += "</td>";
            tableInner += "<td class='text-center'>";
            tableInner += item.PRIX_TOTAL_BLD;
            tableInner += "</td>";
            tableInner += "</tr>";
         })

            tableBl += "<tr class='table-info'>";
            tableBl += "<td class='text-center'>";
            tableBl += res.bon_livraison.CODE_BL;
            tableBl += "</td>";
            tableBl += "<td class='text-center'>";
            tableBl += res.bon_livraison.NOM_CLIENT;
            tableBl += "</td>";
            // tableBl += "<td class='text-center'>";
            // tableBl += res.bon_livraison.STATUS_BL;
            // tableBl += "</td>";
            tableBl += "<td class='text-center'>";
            tableBl += res.bon_livraison.full_name;
            tableBl += "</td>";
            tableBl += "<td class='text-center'>";
            tableBl += res.bon_livraison.DATE_CREATION_BL;
            tableBl += "</td>";
            // tableBl += "<td class='text-center'>";
            // tableBl += "<a href='#' class='btn btn-warning btn-xs'>En attente</a>";
            // tableBl += "</td>";
            tableBl += "</tr>";

            console.log(total)

         $("#tbl").html(tableBl);
         $("#tBlDetail").html(tableInner);    
      })

   }
</script>
<script>
  $(document).ready(function(){
   
    $('.remove-data').click(function(){

      var url = $(this).attr('data-href');

      swal({
          title: "<?= cclang('are_you_sure'); ?>",
          text: "<?= cclang('data_to_be_deleted_can_not_be_restored'); ?>",
         //  type: "input",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "<?= cclang('yes_delete_it'); ?>",
          cancelButtonText: "<?= cclang('no_cancel_plx'); ?>",
          closeOnConfirm: true,
          closeOnCancel: true,
         //  animation:"slide-from-top",
         //  inputPlaceholder: "Donnez un commentaire S.V.P."
        },
      //   function(inputValue){
      //       if (inputValue === false) {
      //          return false;
      //       }
      //       if (inputValue === "") {
      //          swal.showInputError("Vous devriez ecrire un commentaire SVP.!!!");
      //          return false;
      //       }
      //       document.location.href = url +'?inputValue=' +inputValue;
      //     },
          function(isConfirm){
            if (isConfirm) {
               document.location.href = url;      
            }
          });

      return false;
    });


    $('#apply').click(function(){

      var bulk = $('#bulk');
      var serialize_bulk = $('#form_bon_livraison').serialize();

      if (bulk.val() == 'delete') {
         swal({
            title: "<?= cclang('are_you_sure'); ?>",
            text: "<?= cclang('data_to_be_deleted_can_not_be_restored'); ?>",
            type: "input",
            showCancelButton: true,
            confirmButtonColor: "#DD6B55",
            confirmButtonText: "<?= cclang('yes_delete_it'); ?>",
            cancelButtonText: "<?= cclang('no_cancel_plx'); ?>",
            closeOnConfirm: true,
            closeOnCancel: true,
            animation:"slide-from-top",
            inputPlaceholder: "Donnez un commentaire S.V.P."
          },
          function(inputValue){
            if (inputValue === false) {
               return false;
            }
            if (inputValue === "") {
               swal.showInputError("Vous devriez ecrire un commentaire SVP.!!!");
               return false;
            }
            document.location.href = url +'?inputValue=' +inputValue;
          },
          function(isConfirm){
            // if (isConfirm) {
            //    document.location.href = BASE_URL + '/administrator/bon_livraison/delete?' + serialize_bulk;      
            // }
          });

        return false;

      } else if(bulk.val() == '')  {
          swal({
            title: "Upss",
            text: "<?= cclang('please_choose_bulk_action_first'); ?>",
            type: "warning",
            showCancelButton: false,
            confirmButtonColor: "#DD6B55",
            confirmButtonText: "Okay!",
            closeOnConfirm: true,
            closeOnCancel: true
          });

        return false;
      }

      return false;

    });/*end appliy click*/


    //check all
    var checkAll = $('#check_all');
    var checkboxes = $('input.check');

    checkAll.on('ifChecked ifUnchecked', function(event) {   
        if (event.type == 'ifChecked') {
            checkboxes.iCheck('check');
        } else {
            checkboxes.iCheck('uncheck');
        }
    });

    checkboxes.on('ifChanged', function(event){
        if(checkboxes.filter(':checked').length == checkboxes.length) {
            checkAll.prop('checked', 'checked');
        } else {
            checkAll.removeProp('checked');
        }
        checkAll.iCheck('update');
    });

  }); /*end doc ready*/
</script>
