<script type="text/javascript">
   $(document).ready(function() {
    $(document).on('click', '.bt_delete', function() {

      var id = $(this).attr('id');

      swal({

          title: "Êtes-vous sûr?",
          text: "La suppression sera définitive; pas moyen de récupérer!",
          type: "warning",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "Oui, Supprimer-le!",
          cancelButtonText: "Non, Annuler plx ",
          closeOnConfirm: true,
          closeOnCancel: true

        },
        function(isConfirm) {
          if (isConfirm) {
            // console.log(id);
            document.location.href = BASE_URL + 'administrator/voitures/delete/<?=$this->uri->segment(4);?>/'+id;
          }
        }); //end swal

      return false;


    });
   })
</script>