							showCancelButton: true,
							confirmButtonColor: "#DD6B55",
							confirmButtonText: "<?= ('Oui verifier'); ?>",
							cancelButtonText: "<?= ('Non'); ?>",
							closeOnConfirm: true,
							closeOnCancel: true,
							animation: "slide-from-top",
						},
						function(isConfirm) {
							if (isConfirm) {
								var code_commande = $("#code_commande").val();
