<!-- Page script -->
<script>
   function avoid_multi_click_btn(btn_id, period) {
    $('.' + btn_id).attr('disabled', true);

    var my_interval = setInterval(function() {

      $('.' + btn_id).attr('disabled', false);

      clearInterval(my_interval);

    }, period);
  }

  $(document).ready(function(){

    $('.BOUTIQUE').change(function(){
        var boutique_id = $(this).val();
       
        $.ajax({
                url: BASE_URL + '/administrator/fiche_travail/select_famille/<?=$this->uri->segment(4);?>',
                type: 'POST',
                async:false,
                dataType: 'json',
                data: {boutique_id:boutique_id},
                success:function(data)
                {  
                $('.FAMILLE').html(data);
                $('.store').val(boutique_id);              
                }
            });
       });
      $('.FAMILLE').change(function(){
        window.storeuri = $('.BOUTIQUE').val();
        var store = $('.store').val();
        var famille = $(this).val();
    
        $.ajax({
                url: BASE_URL + '/administrator/fiche_travail/select_categorie/'+store,
                type: 'POST',
                async:false,
                dataType: 'json',
                data: {famille:famille},                                                         
                success:function(data)
                {  
                $('.CATEGORIE').html(data);               
                }
            });
       });


    $('.btn_verified').click(function(){

        var id_fiche = $(this).find('input[name=btnsend]').val();
        var boutique = $("#boutique_transfer"+id_fiche).children("option:selected").val();
        var famille = $("#famille_transfer"+id_fiche).children("option:selected").val();
        var categorie = $("#fiche_categorie"+id_fiche).children("option:selected").val();
        var cout = $('#cout'+id_fiche).val();
        var quantite = $('#quantite'+id_fiche).val();
        var prix = $('#prix'+id_fiche).val();
        var description = $('#description'+id_fiche).val();
        
        if(boutique == ''){
          sweetAlert('Donner une boutique');
           return false;
        }else if(famille == ''){
          sweetAlert('Donner une famille');
           return false;
        }else if(quantite == ''){
          sweetAlert('Donner une quantité');
           return false;
        }else if(prix == ''){
          sweetAlert('Donner un prix');
           return false;
        }else if(description == ''){
          sweetAlert('Donner le nom');
           return false;
        }else{

        avoid_multi_click_btn('btn_save', 25000);

        var save_type = $(this).attr('data-stype');
        
        $('.loading').show();
        $.ajax({
            url: BASE_URL + '/administrator/fiche_travail/produit_transfert/<?=$this->uri->segment(4)?>/'+id_fiche,
            type: 'POST',
            dataType: 'json',
            data: {
              id_fiche:id_fiche,
              boutique:boutique,
              famille:famille,
              categorie:categorie,
              cout:cout,
              quantite:quantite,
              prix:prix,
              description:description},
          })

          .done(function(res) {

            if (res.success) {
              if (save_type == 'back') {
                window.location.href = res.redirect;
                return;
              }

              $('.message').printMessage({
                message: res.message
              });
              $('.message').fadeIn();
              resetForm();
              $('.chosen option').prop('selected', false).trigger('chosen:updated');

            } else {

              $('.message').printMessage({
                message: res.message,
                type: 'warning'
              });
            }
          })

          .fail(function() {
            $('.message').printMessage({
              message: 'Error save data',
              type: 'warning'
            });

          })
          .always(function() {

            $('.loading').hide();
            $('html, body').animate({
              scrollTop: $(document).height()
            }, 2000);

          });

        }
      return false;
    });
   
    $('.remove-data').click(function(){

      var url = $(this).attr('data-href');

      swal({
          title: "Etes-vous sûr",
          text: "les données à supprimer ne peuvent pas être restaurées",
          type: "warning",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "oui supprimez-le",
          cancelButtonText: "non annuler plx",
          closeOnConfirm: true,
          closeOnCancel: true
        },
        function(isConfirm){
          if (isConfirm) {
            document.location.href = url;            
          }
        });

      return false;
    });
    $('.approuved-data').click(function(){

      var url = $(this).attr('data-href');

      swal({
          title: "Etes vous sur de vouloir",
          text: "Approuver cette fiche de travail",
          type: "warning",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "oui approuvez-le",
          cancelButtonText: "no annuler plx",
          closeOnConfirm: true,
          closeOnCancel: true
        },
        function(isConfirm){
          if (isConfirm) {
            document.location.href = url;            
          }
        });

      return false;
    });

    $('.desapprouved').click(function(){

      var url = $(this).attr('data-href');

      swal({
          title: "Êtes-vous sûr",
          text: "La fiche de travail correspondante sera décloturer",
          type: "warning",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "oui desapprouvez-le",
          cancelButtonText: "no annuler plx",
          closeOnConfirm: true,
          closeOnCancel: true
        },
        function(isConfirm){
          if (isConfirm) {
            document.location.href = url;            
          }
        });

      return false;
    });

    $('.recancel-data').click(function(){

      var url = $(this).attr('data-href');

      swal({
          title: "Etes-vous sûr",
          text: "les données annuler seront restaurées",
          type: "warning",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "oui restaurez-le",
          cancelButtonText: "no annuler plx",
          closeOnConfirm: true,
          closeOnCancel: true
        },
        function(isConfirm){
          if (isConfirm) {
            document.location.href = url;            
          }
        });

      return false;
    });

  }); /*end doc ready*/
</script>