      $('#CATEGORIE_DEPENSE').val("");
      $('#DESCRIPTION_DEPENSE').val("");
      $('#DESCRIPTION_DEPENSE').val("");
      $('#MONTANT_DEPENSE').val();
      $('#TYPE_ACTIVITE').val("");
      $('NOM_DEPENSE');
   })


   $('.btn_save').click(function() {
      $('.message').fadeOut();

      var form_pos_depenses = $('#form_pos_depenses_add');
      var data_post = form_pos_depenses.serializeArray();
      var save_type = $(this).attr('data-stype');

      data_post.push({
         name: 'save_type',
         value: save_type
      });
      $('.loading').show();

      $.ajax({
            url: BASE_URL + '/administrator/pos_depenses/add_save',
            type: 'POST',
            dataType: 'json',
            data: data_post,
         })
         .done(function(res) {
            if (res.success) {

               $('#table_depense').load(' #table_depense');
               $('#nouvelle_depense').modal('hide');
               $('.message').css('display', 'none');

               toastr['success']('Depense ajouter!!');
               $('#TYPE_ACTIVITE').val('');
               // $('.message').fadeOut();



               if (save_type == 'back') {
                  window.location.href = res.redirect;
                  return;
               }

               // $('.message').printMessage({
               //   message: res.message
               // });
               // $('.message').fadeIn();
               resetForm();
               $('.chosen option').prop('selected', false).trigger('chosen:updated');

            } else {
               $('.message').printMessage({
                  message: res.message,
                  type: 'warning'
               });
            }

         })
         .fail(function() {
            $('.message').printMessage({
               message: 'Error save data',
               type: 'warning'
            });
         })
         .always(function() {
            $('.loading').hide();
            $('html, body').animate({
               scrollTop: $(document).height()
            }, 2000);
         });

      return false;
   }); /*end btn save*/




   function get_depenses(th) {
      let id = $(th).attr('id');

      $.ajax({
         url: BASE_URL + '/administrator/pos_depenses/get_depenses',
         type: 'POST',
         dataType: 'json',
         data: {
            id: id
         },
         success: function(dt) {
            console.log(dt);
            $("#NOM_DEPENSE_UP").val(dt.NOM_DEPENSE);
            $("#TYPE_ACTIVITE_UP").val(dt.TYPE_ACTIVITE_CAISSE);
            $("#DESCRIPTION_DEPENSE_UP").val(dt.DESCRIPTION_DEPENSE);
            $("#ID_CATEGORIE_DEPENSE_UP").val(dt.ID_CATEGORIE_DEPENSE);
            $('#ID_DEPENSE_UP').val(dt.ID_DEPENSE);

            if(dt.TYPE_ACTIVITE_CAISSE ==2){
               
               $("#MONTANT_DEPENSE_UP").val(dt.MONTANT_APPROVIONNEMENT);
              }
              else {
              
               $("#MONTANT_DEPENSE_UP").val(dt.MONTANT_DEPENSE);

              }


         }
      })

   }






