<script>
  $(document).ready(function(){
   
    $('.remove-data').click(function(){

      var url = $(this).attr('data-href');

      swal({
          title: "<?= cclang('are_you_sure'); ?>",
          text: "<?= cclang('data_to_be_deleted_can_not_be_restored'); ?>",
          type: "input",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "<?= cclang('yes_delete_it'); ?>",
          cancelButtonText: "<?= cclang('no_cancel_plx'); ?>",
          closeOnConfirm: true,
          closeOnCancel: true,
          animation:"slide-from-top",
          inputPlaceholder: "Donnez un commentaire S.V.P."
        },
        function(inputValue){
            if (inputValue === false) {
               return false;
            }
            if (inputValue === "") {
               swal.showInputError("Vous devriez ecrire un commentaire SVP.!!!");
               return false;
            }
            document.location.href = url +'?inputValue=' +inputValue;
          },
          function(isConfirm){
            if (isConfirm) {
               document.location.href = BASE_URL + '/administrator/pos_ibi_menu_du_jous/delete?' + serialize_bulk;      
            }
          });

      return false;
    });


    $('#apply').click(function(){

      var bulk = $('#bulk');
      var serialize_bulk = $('#form_pos_ibi_menu_du_jous').serialize();

      if (bulk.val() == 'delete') {
         swal({
            title: "<?= cclang('are_you_sure'); ?>",
            text: "<?= cclang('data_to_be_deleted_can_not_be_restored'); ?>",
            type: "input",
            showCancelButton: true,
            confirmButtonColor: "#DD6B55",
            confirmButtonText: "<?= cclang('yes_delete_it'); ?>",
            cancelButtonText: "<?= cclang('no_cancel_plx'); ?>",
            closeOnConfirm: true,
            closeOnCancel: true,
            animation:"slide-from-top",
            inputPlaceholder: "Donnez un commentaire S.V.P."
          },
          function(inputValue){
            if (inputValue === false) {
               return false;
            }
            if (inputValue === "") {
               swal.showInputError("Vous devriez ecrire un commentaire SVP.!!!");
               return false;
            }
            document.location.href = url +'?inputValue=' +inputValue;
          },
          function(isConfirm){
            // if (isConfirm) {
            //    document.location.href = BASE_URL + '/administrator/pos_ibi_menu_du_jous/delete?' + serialize_bulk;      
            // }
          });

        return false;

      } else if(bulk.val() == '')  {
          swal({
            title: "Upss",
            text: "<?= cclang('please_choose_bulk_action_first'); ?>",
            type: "warning",
            showCancelButton: false,
            confirmButtonColor: "#DD6B55",
            confirmButtonText: "Okay!",
            closeOnConfirm: true,
            closeOnCancel: true
          });

        return false;
      }

      return false;

    });/*end appliy click*/


    //check all
    var checkAll = $('#check_all');
    var checkboxes = $('input.check');

    checkAll.on('ifChecked ifUnchecked', function(event) {   
        if (event.type == 'ifChecked') {
            checkboxes.iCheck('check');
        } else {
            checkboxes.iCheck('uncheck');
        }
    });

    checkboxes.on('ifChanged', function(event){
        if(checkboxes.filter(':checked').length == checkboxes.length) {
            checkAll.prop('checked', 'checked');
        } else {
            checkAll.removeProp('checked');
        }
        checkAll.iCheck('update');
    });

  }); /*end doc ready*/

  $(".desactive_menu").on('click',function () {
     var id_menu = $(this).attr('data-id');
     var type = 'back';
     var store = <?php echo $this->uri->segment(2);?>;
      swal({
            title: "Attention",
            text: "La desactivation de ce menu entrainera sa suppression au point de vente !",
            showCancelButton: true,
            confirmButtonColor: "#DD6B55",
            cancelButtonText: "Fermer",
            closeOnConfirm: true,
            closeOnCancel: true,
            animation: "slide-from-top",
             },
             function(isConfirm){
                if (isConfirm) {
                       $.ajax({
                        url: BASE_URL + '/administrator/pos_ibi_menu_du_jous/update_menu',
                        type: 'POST',
                        dataType: 'json',
                        data:{id_menu:id_menu,save_type:type,store:store},
                      })
                      .done(function(res) {
                        if(res.success) {
                        //   if (save_type == 'back') {
                            window.location.href = res.redirect;
                        //     return;
                        //   }
                  
                          $('.message').printMessage({message : res.message});
                          $('.message').fadeIn();
                          $('.data_file_uuid').val('');
                  
                        } else {
                          $('.message').printMessage({message : res.message, type : 'warning'});
                        }
                  
                      })
                      .fail(function() {
                        $('.message').printMessage({message : 'Error save data', type : 'warning'});
                      })
                      .always(function() {
                        $('.loading').hide();
                        $('html, body').animate({ scrollTop: $(document).height() }, 2000);
                      });
                }
              }
             
             
             );
  });

    $(".active_menu").on('click',function () {
     var id_menu = $(this).attr('data-id');
     var type = 'back';
     var store = <?php echo $this->uri->segment(2);?>;
      swal({
            title: "Information",
            text: "Vous-le vous vraiment desactivez c'est menu  !",
            showCancelButton: true,
            confirmButtonColor: "#DD6B55",
            cancelButtonText: "Fermer",
            closeOnConfirm: true,
            closeOnCancel: true,
            animation: "slide-from-top",
             },
             function(isConfirm){
                if (isConfirm) {
                       $.ajax({
                        url: BASE_URL + '/administrator/pos_ibi_menu_du_jous/active_menu',
                        type: 'POST',
                        dataType: 'json',
                        data:{id_menu:id_menu,save_type:type,store:store},
                      })
                      .done(function(res) {
                        if(res.success) {
                        //   if (save_type == 'back') {
                            window.location.href = res.redirect;
                        //     return;
                        //   }
                  
                          $('.message').printMessage({message : res.message});
                          $('.message').fadeIn();
                          $('.data_file_uuid').val('');
                  
                        } else {
                          $('.message').printMessage({message : res.message, type : 'warning'});
                        }
                  
                      })
                      .fail(function() {
                        $('.message').printMessage({message : 'Error save data', type : 'warning'});
                      })
                      .always(function() {
                        $('.loading').hide();
                        $('html, body').animate({ scrollTop: $(document).height() }, 2000);
                      });
                }
              }
             
             
             );
  })
</script>
