<script>
   $(document).ready(function() {

      $('.remove-data').click(function() {

         var url = $(this).attr('data-href');

         swal({
               title: "Suppression ",
               text: "Vous etes sur le point de suprimer cette article!!..",
               type: "input",
               showCancelButton: true,
               confirmButtonColor: "#DD6B55",
               confirmButtonText: "Oui, supprimer",
               cancelButtonText: "Non, annuler",
               closeOnConfirm: false,
               closeOnCancel: true,
               animation: "slide-from-top",
               inputPlaceholder: "Donnez un commentaire S.V.P."
            },
            function(isConfirm) {
               if (isConfirm == "") {
                  swal.showInputError("Vous devriez ecrire un commentaire SVP.!!!");
                  return false;
               } else {


                  // swal.closeOnCancel = true;
                  document.location.href = url + '?inputValue=' + isConfirm;
                  //  document.location.href = url+'/'+isConfirm;   
                  // console.log(url+'/'+isConfirm); 
               }
            });

         return false;
      });










      $('#apply').click(function() {

         var bulk = $('#bulk');
         var serialize_bulk = $('#form_req').serialize();

         if (bulk.val() == 'delete') {
            swal({
                  title: "<?= cclang('are_you_sure'); ?>",
                  text: "<?= cclang('data_to_be_deleted_can_not_be_restored'); ?>",
                  type: "input",
                  inputPlaceholder: 'veillez signaler la raison de la suppresion',
                  showCancelButton: true,
                  confirmButtonColor: "#DD6B55",
                  confirmButtonText: "<?= cclang('yes_delete_it'); ?>",
                  cancelButtonText: "<?= cclang('no_cancel_plx'); ?>",
                  closeOnConfirm: true,
                  closeOnCancel: true
               },
               function(isConfirm) {
                  if (isConfirm) {
                     console.log(serialize_bulk);
                     return
                     document.location.href = BASE_URL + '/administrator/requisition/delete?' + serialize_bulk;
                  }

               });

            return false;

         } else if (bulk.val() == '') {
            swal({
               title: "Upss",
               text: "<?= cclang('please_choose_bulk_action_first'); ?>",
               type: "warning",
               showCancelButton: false,
               confirmButtonColor: "#DD6B55",
               confirmButtonText: "Okay!",
               closeOnConfirm: true,
               closeOnCancel: true
            });

            return false;
         }

         return false;

      }); /*end appliy click*/


      //check all
      var checkAll = $('#check_all');
      var checkboxes = $('input.check');

      checkAll.on('ifChecked ifUnchecked', function(event) {
         if (event.type == 'ifChecked') {
            checkboxes.iCheck('check');
         } else {
            checkboxes.iCheck('uncheck');
         }
      });

      checkboxes.on('ifChanged', function(event) {
         if (checkboxes.filter(':checked').length == checkboxes.length) {
            checkAll.prop('checked', 'checked');
         } else {
            checkAll.removeProp('checked');
         }
         checkAll.iCheck('update');
      });

   }); /*end doc ready*/





   function bs_Viewers(th) {


      let ider = $(th).attr('id');
      $.ajax({
         url: BASE_URL + 'administrator/requisition/incomming/' + ider,
         type: 'get',
         data: {
            ider: ider
         },
         success: function(dt) {
            if (dt) {
               //  location.reload(true);
               //  $('#tbody_req').load('#tbody_req');
            } else {
               location.reload(true);
            }
         }
      });
   }


