            confirmButtonColor: "#DD6B55",
            confirmButtonText: "confirmer!",
            cancelButtonText: "Non!",
            closeOnConfirm: true,
            closeOnCancel: true
         },
         function(isConfirm) {

            $.ajax({
               url: url,
               method: 'post',
               dataType: 'json',
               data: {
                  "<?php echo $this->security->get_csrf_token_name(); ?> ": " <?php echo $this->security->get_csrf_hash(); ?>"
               },
