<script type="text/javascript">
  var prix=0, idproduit=0, qtproduit=0, types='', store="<?=$this->uri->segment(2)?>";

  $('#btn-ajouter').click(function(){

       qtproduit=$('#quantite').val();
       $.ajax({
        type: 'post',
        url: BASE_URL + '/administrator/sortie/modifyitem',
        data: {idproduit:idproduit, qtproduit:qtproduit,store:store,prix:prix, types:types},
        success:function(data){
          
          location.reload();
        }
    });
      
    }); 

  $('.update-data').click(function(){
     $('#ExModal').modal('show');
     $('#quantite').val($(this).attr('data-qt'));
     idproduit=$(this).attr('data-id');
     prix=$(this).attr('data-prix');
     types=$(this).attr('data-types');
     // $('#quantite').attr('max',$(this).attr('data-max'))
      
    });
  
  $('.remove-data').click(function(){

      var url = $(this).attr('data-href');

      swal({
          title: "<?= cclang('are_you_sure'); ?>",
          text: "<?= cclang('data_to_be_deleted_can_not_be_restored'); ?>",
          type: "input",
          showCancelButton: true,
          confirmButtonColor: "#DD6B55",
          confirmButtonText: "<?= cclang('yes_delete_it'); ?>",
          cancelButtonText: "<?= cclang('no_cancel_plx'); ?>",
          closeOnConfirm: true,
          closeOnCancel: true,
          animation:"slide-from-top",
          inputPlaceholder: "Donnez un commentaire S.V.P."
        },
        function(inputValue){
            if (inputValue === false) {
               return false;
            }
            if (inputValue === "") {
               swal.showInputError("Vous devriez ecrire un commentaire SVP.!!!");
               return false;
            }
            document.location.href = url +'?inputValue=' +inputValue;
          },
          function(isConfirm){
            // if (isConfirm) {
            //    document.location.href = BASE_URL + '/administrator/sortie/delete?' + serialize_bulk;      
            // }
          });

      return false;
    });

</script>